export function SkillCard({ category, index }: SkillCardProps) {
  return (
    <motion.div
      initial={{ opacity: 0, y: 60 }}
      whileInView={{ opacity: 1, y: 0 }}
      viewport={{ once: true, amount: 0.1 }}
      transition={{
        duration: 1,
        delay: index * 0.15,
        ease: [0.165, 0.84, 0.44, 1] as const,
      }}
      className="bg-dark-secondary border border-white/10 rounded-3xl p-6 md:p-8 card-hover"
    >
      <div className="flex items-center gap-3 mb-6">
        <div className="p-3 bg-coral/10 rounded-xl text-coral">
          {categoryIcons[category.title] || <Boxes className="w-6 h-6" />}
        </div>
        <h3 className="text-lg md:text-xl font-semibold uppercase tracking-tight text-white">
          {category.title}
        </h3>
      </div>

      <div className="space-y-3">
        {category.skills.map((skill, skillIndex) => (
          <motion.div
            key={skill.name}
            initial={{ opacity: 0, x: -20 }}
            whileInView={{ opacity: 1, x: 0 }}
            viewport={{ once: true }}
            transition={{
              duration: 0.5,
              delay: index * 0.15 + skillIndex * 0.05,
              ease: [0.165, 0.84, 0.44, 1] as const,
            }}
            whileHover={{ scale: 1.02 }}
            className="flex items-center gap-3 p-3 rounded-xl bg-white/5 hover:bg-white/10 transition-colors duration-200 group cursor-default"
          >
            <motion.div
              whileHover={{ scale: 1.15 }}
              transition={{ duration: 0.2 }}
              className="text-white/50 group-hover:text-coral transition-colors duration-200"
            >
              {techIcons[skill.icon] || <Code className="w-5 h-5" />}
            </motion.div>
            <span className="text-sm md:text-base text-white/70 group-hover:text-white transition-colors duration-200">
              {skill.name}
            </span>
          </motion.div>
        ))}
      </div>
    </motion.div>
  );
}
